function e(e){e.innerHTML='\n    <div class="login-background">\n      <section class="login-card">\n        <h2 class="login-title">ğŸ“ Registro</h2>\n        <form id="register-form" class="login-form">\n          <label>Usuario</label>\n          <input id="reg-username" required />\n          <div style="display:flex;gap:8px;justify-content:flex-end;margin-top:12px;">\n            <button class="btn-publicar" type="submit">Registrar (visitante)</button>\n          </div>\n        </form>\n      </section>\n    </div>\n  ',document.getElementById("register-form").addEventListener("submit",e=>{e.preventDefault();const n=document.getElementById("reg-username").value.trim();if(!n)return;const t={username:n,role:"visitante"};localStorage.setItem("user",JSON.stringify(t)),location.reload()})}export{e as renderRegister};
